import { Link, useParams } from 'react-router-dom'
import Navigators from './Navigators/Navigators'
import user from '../img/Userpic.png'
import './Videopage.css'
import UserVideo from './Videos/userVideo/UserVideo'
import { useState, useEffect } from 'react'
import axios from 'axios'
const VideoPage = () => {
  const { id } = useParams()
  const [data, setData] = useState([])

  const fetching = async () => {
    const options = {
      method: 'GET',
      url: 'https://youtube-v3-alternative.p.rapidapi.com/trending',
      params: {
        geo: 'US',
        lang: 'en',
      },
      headers: {
        'X-RapidAPI-Key': '7da83e8cdemsh71c5e2d155f1a08p188b14jsnd707f0b66054',
        'X-RapidAPI-Host': 'youtube-v3-alternative.p.rapidapi.com',
      },
    }

    try {
      const response = await axios.request(options)
      const baseData = response.data
      setData(baseData.data)
    } catch (error) {
      console.error(error)
    }
  }

  useEffect(() => {
    fetching()
  }, [])
  return (
    <div>
      <Navigators />

      <div className='single__video w-4/5 h-screen'>
        <div className='navbar  w-full h-14'>
          <div className='input__bar w-3/5'>
            <input type='text' placeholder='Search...' />
          </div>

          <div className='details'>
            <img src={user} alt='' />
          </div>
        </div>

        <div className='simpleSingle'>
          <iframe
            className='iframe'
            src={`https://www.youtube.com/embed/${id}`}
            frameborder='0'
            autoFocus
          ></iframe>

          <div className='offer__videos__singlePg'>
            {data.map((videoItem) => (
              <UserVideo
                id={videoItem.videoId}
                img={videoItem.thumbnail[2].url}
              />
            ))}
            <UserVideo />
          </div>
        </div>
      </div>
    </div>
  )
}
export default VideoPage
